pair Sp=RS[0];
pair R=RS[1];
pair[] dots={A,B,T,Sp,R,C};
dot(dots);
draw(Circle(A,1));
draw(B--T--R);
label("$T$",T,S);
label("$A$",A,S);
label("$B$",B,W);
label("$R$",R,NW);
label("$S$",Sp,NE);
label("$C$",C,SE);[/asy]

